var UIBootbox = function() {
    var o = function() {
        $("#demo_1").click(function() {
            bootbox.alert("Hello world!")
        }), $("#demo_2").click(function() {
            bootbox.alert("Hello world!", function() {
                $.notify({
                    icon: 'glyphicon glyphicon-exclamation-sign',
                    message: "Hello world callback"
                });
            })
        }), $("#demo_3").click(function() {
            bootbox.confirm("Are you sure?", function(o) {
                $.notify({
                    icon: 'glyphicon glyphicon-exclamation-sign',
                    message: "Confirm result: "+ o
                });
            })
        }), $("#demo_4").click(function() {
            bootbox.prompt("What is your name?", function(o) {
                null === o ? alert("Prompt dismissed") : alert("Hi <b>" + o + "</b>")
            })
        }), $("#demo_5").click(function() {
            bootbox.dialog({
                message: "I am a custom dialog",
                title: "Custom title",
                buttons: {
                    success: {
                        label: "Success!",
                        className: "btn-success",
                        callback: function() {
                            $.notify({
                                icon: 'glyphicon glyphicon-exclamation-sign',
                                message: "great success"
                            });
                        }
                    },
                    danger: {
                        label: "Danger!",
                        className: "btn-danger",
                        callback: function() {
                            $.notify({
                                icon: 'glyphicon glyphicon-exclamation-sign',
                                message: "uh oh, look out!"
                            });
                        }
                    },
                    main: {
                        label: "Click ME!",
                        className: "btn-primary",
                        callback: function() {
                            $.notify({
                                icon: 'glyphicon glyphicon-exclamation-sign',
                                message: "Primary button"
                            });
                        }
                    }
                }
            })
        }), $("#demo_6").click(function() {
            bootbox.dialog({
                title: "That html",
                message: '<div class="media">' +
                '<div class="media-left">' +
                '<img class="media-object img-lg img-circle" src="../../assets/images/avatar.png" alt="Profile picture">' +
                '</div><div class="media-body"><h4 class="text-thin">You can also use <strong>html</strong></h4>Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.</div></div>',
                buttons: {
                    confirm: {
                        label: "Save"
                    }
                }
            })
        })
    };
    return {
        init: function() {
            o()
        }
    }
}();
jQuery(document).ready(function() {
    UIBootbox.init()
});
